// to make the other regex more complicated.
/*
text = text.replace(/
(						// save in $1
\n\n				// Starting after a blank line
[ ]{0,3}
(<(hr)				// start tag = $2
\b					// word break
([^<>])*?			//
\/?<?php echo ""; ?>>)				// the matching end tag
[ \t]*
(?=\n{2,})			// followed by a blank line
)
/g,hashElement);
*/
