<script>
function createPage(name) {
if(name=="") {
alert("<?php echo trans("Bitte geben Sie der neuen Seite einen Namen!", "Please name your new sheet!");?>");
} else {
$.ajax({
"url": "<?php echo FILENAME;?>",
"type": "post",
"data": {"action": "newpage", "name": name},
"dataType": "json",
"success": function(data) {
if(data.result==1) {
window.location = '<?php echo FILENAME;?>?ID='+data.key;
} else {
alert("Ein Fehler ist aufgetreten.");
}}
});
}
return false;
}
var thisID = "";
var onLoadEditID=0;
var newestBlock = 0;
function loadPage(ID) {
thisID = ID;
$.ajax({
"url": "<?php echo FILENAME;?>",
"type": "post",
"data": {"action": "loadpage", "id": ID},
"dataType": "json",
"success": function(data) {
//console.log(data);
if(data.result==1) {
//$('#titel').html(data.data.name);
var B = data.data.blocks;
if(B.length>0) $('#blocks').html("");
for(var i=0;i<B.length;i++) {
var html = getBB(B[i]);
$('#blocks').append(html);
}
updateBlockDo();
} else {
alert("Ein Fehler ist aufgetreten.");
}}
});
}
function updateBlockDo() {
$('.blockDo').html("");
$('.blockDo').each(function() {
var rel = $(this).attr("rel");
$(this).append("<a href='#' onclick='removeBlock(this);return false;' style='float:left;' class='blockicon'><i class='bigglyph glyphicon glyphicon-remove-circle'></i></a>");
$(this).append("<a href='#' onclick='moveDown(this);return false;' style='float:right;' class='blockicon'><i class='bigglyph glyphicon glyphicon-circle-arrow-down'></i>&nbsp;</a>");
$(this).append("<a href='#' onclick='moveUp(this);return false;' style='float:right;' class='blockicon'><i class='bigglyph glyphicon glyphicon-circle-arrow-up'></i>&nbsp;</a>");
$(this).append("<a href='#' onclick='editBlock(this);return false;' style='float:right;' class='blockicon'><i class='bigglyph glyphicon glyphicon-pencil'></i>&nbsp;</a>");
$(this).append("<div style='clear:both;'></div>");
});
}
function getBB(B) {
var html = '<div class="row qcline"  id="BB'+B.id+'">';
html += '<div class="col-lg-2 blockDo" rel="'+B.id+'"></div>';
html += '<div class="col-lg-8">';
html += "<div class='oneblock' id='B"+B.id+"'>";
html += B.info;
if(B.changedts>newestBlock) newestBlock = B.changedts;
if(typeof(B.locked)=="undefined" || B.locked=="") {
} else {
if(B.locked=="<?php echo $_COOKIE["EasyPad"];?>") {
onLoadEditID = B.id;
setTimeout(function() { editBlockID(onLoadEditID) }, 100);
}}
html += "</div>";
html += '</div>';
html += '<div class="col-lg-2 blockInfo" style="border-bottom:solid 1px #dfdfdf;">';
html += "<div style='display:none;' class='changedates'>";
html += "<div style='float:left;padding-right:10px;'><?php echo trans("erstellt von", "created by");?>: <nobr>"+B.creator+"</nobr></div>";
if(typeof(B.changer)!="undefined") html += "<div style='float:left;padding-right:10px;'><?php echo trans("zuletzt gendert von", "last changed by");?>: <nobr>"+B.changer+"</nobr></div>";
html += "<div style='float:left;padding-right:10px;'><?php echo trans("gendert am", "changed at");?>: <nobr>"+B.changed+"</nobr></div>";
html += "<div style='clear:both;'></div>";
html += "</div>";
<?php if(isset($_SESSION["user"]) && $_SESSION["user"]!="") { ?>
html += "<i>Kommentar </i><a href='#' onclick=\"addComment('"+B.id+"');return false;\"><i class='glyphicon glyphicon-plus-sign'></i></a>";
if(typeof(B.comments)!="undefined") {
for(var i=0;i<B.comments.length;i++) {
html += '<div class="comments">'+ B.comments[i]+'</div>';
}}
html += '<div class="commentform"></div>';
<?php } ?>
html += '</div>';
return html;
}
<?php if(isset($_SESSION["user"]) && $_SESSION["user"]!="") { ?>
function addComment(id) {
var form = '<form onsubmit="saveComment(\''+id+'\');return false">';
form += '<textarea class="form-control" rows=3 id="comment'+id+'"></textarea>';
form += '<button class="btn btn-default btn-xs">speichern</button>';
form += '<button class="btn btn-default btn-xs" onclick="removeCommentForm(\''+id+'\');return false;">verwerfen</button>';
