var vh = "visible";
(function() {
var hidden = "hidden";
// Standards:
if (hidden in document) document.addEventListener("visibilitychange", onchangehiddenvisible);
else if ((hidden = "mozHidden") in document) document.addEventListener("mozvisibilitychange", onchangehiddenvisible);
else if ((hidden = "webkitHidden") in document) document.addEventListener("webkitvisibilitychange", onchangehiddenvisible);
else if ((hidden = "msHidden") in document) document.addEventListener("msvisibilitychange", onchangehiddenvisible);
else if ('onfocusin' in document) document.onfocusin = document.onfocusout = onchangehiddenvisible;
else window.onpageshow = window.onpagehide = window.onfocus = window.onblur = onchangehiddenvisible;
function onchangehiddenvisible (evt) {
var v = 'visible', h = 'hidden',
evtMap = {
focus:v, focusin:v, pageshow:v, blur:h, focusout:h, pagehide:h
};
evt = evt || window.event;
if (evt.type in evtMap) {
vh = evtMap[evt.type];
} else {
vh = this[hidden] ? "hidden" : "visible";
}
//console.log(vh);
}})();
var progressNr = 0;
// http://www.developphp.com/view.php?tid=1351
function uploadFile(obj, id) {
progressNr++;
progress = '<progress id="progressBar' + progressNr + '" value="0" max="100" style="width:300px;"></progress>';
$(obj).closest("div").append(progress);
var file = obj.files[0];
var formdata = new FormData();
formdata.append("file", file);
var ajax = new XMLHttpRequest();
progressData = {
"id": 'progressBar' + progressNr,
"textid": id,
"name": file.name,
"progress": function(event) {
var percent = (event.loaded / event.total) * 100;
$('#' + this.id).val(Math.round(percent));
},
"done": function(event) {
$('#' + this.textid + "_file").val("");
//$('#' + this.textid + "_info").html("File: " + this.name + " / " + file.size + " Byte");
$('#' + this.textid).val( $('#' + this.textid).val()+"\n"+event.target.responseText);
$('#' + this.id).remove();
}};
ajax.upload.addEventListener("progress", $.proxy(progressData.progress, progressData), false);
ajax.addEventListener("load", $.proxy(progressData.done, progressData), false);
ajax.addEventListener("error", function(event) {
}, false);
ajax.addEventListener("abort", function(event) {
}, false);
ajax.open("POST", "<?php echo FILENAME;?>?id=<?php echo $_REQUEST["ID"];?>&action=addfile2");
ajax.send(formdata);
}
